import React, { useEffect, useState } from "react";
import Movie from "../../../components/Movie/Movie";
import SearchBar from "../../../components/Searchbar/Searchbar";
const GeneralMovies = (props) => {
  const [stateIds, setStateIds] = useState([]);
  const [stateMovies, setStateMovies] = useState([]);
  const [inputEntered, setInputEntered] = useState(true);
  let searchInput = "hero";
  //   const useFetch = (searchInput) => {
  useEffect(async () => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    // let myMovies = [];
    // let finalMovies = [];
    let urlString = "";
    // let searchInput = props.searchBar.props.onChange();
    console.log(props.baseSearchUrl + "api" + props.API_KEY_SEARCH);
    if (searchInput !== "")
      urlString = props.baseSearchUrl.concat(searchInput, props.API_KEY_SEARCH);

    console.log(urlString);
    await fetch(urlString) // need to give urlString
      .then((res) => res.json())
      .then((json) => {
        // console.log(json);
        for (const [i, result] of json.results.entries()) {
          // console.log("index" + i + "value" + result.id);
          id_info[i] = result.id;
          //   console.log(result);
          //   const myId = result.id;
          //   setStateIds((prevIds) => [...id_info]);
        }
        // id_info = json.results.map((result) => {
        //   return result.id; // returning id's of movies
        // });
      });
    // .catch((error) => console.log(error));
    console.log(id_info);
    // console.log(inputReady);
    for (const [i, id] of id_info.entries()) {
      movieUrl = props.baseMovieUrl + id + props.API_KEY_MOVIE;
      //   console.log("i? " + i + " val? " + id);
      myMovies[i] = await fetch(movieUrl)
        .then((res) => res.json())
        .then((json) => {
          // console.log(json);
          //   console.log("index " + i + "val " + json);
          return json;
        });
      //   setStateMovies(myMovies);
      //   console.log(stateMovies);
    }
    console.log(myMovies);
    setStateMovies(myMovies);
    // console.log(stateMovies);
    setInputEntered(true);
  }, []);
  //   };

  let imgUrl = "https://image.tmdb.org/t/p/w185/";
  let movies = null;
  let myPath = "";
  let inputEnablerMsg = null;
  let searchBar = null;
  // const starterFunc = () => {
  // if(this.state.)
  //   if (props.baseSearchUrl) searchBar = <SearchBar onChange={searchContent} />;

  if (inputEntered) {
    // console.log(stateMovies);
    // const presentMovies = stateMovies;
    console.log(stateMovies);
    movies = (
      <div>
        {stateMovies.map((movie, index) => {
          if (movie.poster_path) myPath = movie.poster_path;
          else if (movie.backdrop_path) myPath = movie.backdrop_path;
          else return <p key={index}> NO IMAGE! sorry :(</p>;
          return (
            // <div style={{ display: "flex" }}>
            <Movie
              key={movie.id}
              title={movie.title}
              image={imgUrl + myPath}
              description={movie.overview}
              name={movie.title}
              rating={movie.vote_average}
            />
            // {console.log(movie.title)}
            // </div>
          );
        })}
      </div>
    );
    console.log(movies);
  } else {
    inputEnablerMsg = <p>Please! enter a search input above! :)</p>;
  }

  return (
    <div>
      {movies}
      {searchBar}
    </div>
  );
};

export default GeneralMovies;
