import React, { Component } from "react";
import Movie from "../../components/Movie/Movie";
// import Button from "../../components/Button/Button";
import classes from "./Movies.module.css";
import Searchbar from "../../components/Searchbar/Searchbar";
// import Rating from "../../components/Movie/Rating/Rating";
class Movies extends Component {
  state = {
    movies: [],
    movies_ids: [],
    showMovies: true,
    inputEntered: true,
  };

  componentDidMount() {
    this.fetchingMovies("", true);
    this.fetchingMovies("", false);
  }

  fetchingMovies = async (searchInput, popCheck) => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    let urlString = "";

    //  console.log(urlString);
    if (searchInput !== "" || popCheck) {
      if (popCheck)
        urlString =
          "https://api.themoviedb.org/3/movie/top_rated?api_key=d0a9ee76df15c9c8e455000b78e70a56";
      else
        urlString =
          "https://api.themoviedb.org/3/search/movie?query=" +
          searchInput +
          "&api_key=d0a9ee76df15c9c8e455000b78e70a56";

      await fetch(urlString)
        .then((res) => res.json())
        .then((json) => {
          console.log(json);
          id_info = json.results.map((result) => {
            return result.id; // returning id's of movies
          });
        })
        .catch((error) => console.log(error));
      // console.log(id_info);
      this.setState({
        movies_ids: id_info, // setting an array of id's
      });
      this.state.movies_ids.map(async (id, index) => {
        movieUrl =
          "https://api.themoviedb.org/3/movie/" +
          id +
          "?api_key=d0a9ee76df15c9c8e455000b78e70a56"; // creating a movie from the id given
        myMovies[index] = await fetch(movieUrl)
          .then((res) => res.json())
          .then((json) => {
            // console.log(json);
            return json; // returning movie at an index location
          });
        // .catch((error) => {
        //   console.log(error);
        // })
        console.log(this.state.movies);
        this.setState({
          movies: myMovies,
        });
      });

      this.setState({ inputEntered: true });
    } else {
      const inputEnabler = this.state.inputEntered;
      this.setState({ inputEntered: !inputEnabler });
    }
    // .catch((error) => {
    //   console.log(error);
    // })

    // console.log(myMovies);
  };

  // fetchingPopularMovies = async () => {
  //   let popUrl =
  //     "https://api.themoviedb.org/3/movie/popular?api_key=d0a9ee76df15c9c8e455000b78e70a56";
  //   fetch(popUrl)
  //   .then(res => console.);
  // };
  searchContent = (event) => {
    this.fetchingMovies(event.target.value, false);
  };
  presentPopularMovies = () => {
    this.fetchingMovies("", true);
  };
  // presentMovies = () => {
  //   const doesShow = this.state.showMovies;
  //   // this.setState({ showMovies: !doesShow });
  // };

  // getImgUrl = (baseUrl, path){

  //   if(path)
  // }
  render() {
    let imgUrl = "https://image.tmdb.org/t/p/w185/";

    let movies = null;
    let myPath = "";
    let inputEnablerMsg = null;
    if (this.state.inputEntered) {
      movies = (
        <div>
          {this.state.movies.map((movie, index) => {
            if (movie.poster_path) myPath = movie.poster_path;
            else if (movie.backdrop_path) myPath = movie.backdrop_path;
            else return <p> NO IMAGE! sorry :(</p>;
            return (
              <div key={movie.id} style={{ display: "flex" }}>
                <Movie
                  title={movie.title}
                  image={imgUrl + myPath}
                  description={movie.overview}
                  name={movie.title}
                  rating={movie.vote_average}
                />
              </div>
            );
          })}
        </div>
      );
    } else {
      inputEnablerMsg = <p>Please! enter a search input above! :)</p>;
    }

    return (
      <div className={classes.Movies}>
        {/* <Button name={"Show Movies"} myFunction={this.presentMovies} /> */}
        {/* <input type="text" onChange={this.searchContent.bind(this)} /> */}

        <Searchbar onChange={this.searchContent.bind(this)}></Searchbar>
        {movies}
        {inputEnablerMsg}
      </div>
    );
  }
}
export default Movies;
