import React, { useState } from "react";
import Movie from "../../../components/Movie/Movie";
import useFetch from "./useFetch";
import classes from "./GeneralMovies.module.css";
const GeneralMovies = (props) => {
  const [stateMovies, setStateMovies] = useState([]);
  const [inputEntered, setInputEntered] = useState(false);
  let searchInput = "captain";
  let inputEnablerMsg = null;
  let movies = null;
  let imgUrl = "https://image.tmdb.org/t/p/w185/";
  let myPath = "";
  let data = null;
  let style = null;

  data = useFetch(props, searchInput);
  Promise.resolve(data).then((val) => {
    if (val.length) {
      setInputEntered(true);
      setStateMovies(val);
    }
  });
  if (!props.isSearched) {
    style = { display: "flex" };
    stateMovies.splice(8, stateMovies.length - 8);
  }
  if (inputEntered) {
    movies = (
      <div style={style}>
        {stateMovies.map((movie, index) => {
          if (movie.poster_path) {
            myPath = movie.poster_path;
            console.log(movie);
          } else if (movie.backdrop_path) myPath = movie.backdrop_path;
          else return stateMovies[index + 1];
          return (
            <div className={classes.MoviesFlex}>
              <Movie
                isSearched={props.isSearched}
                key={movie.id}
                title={movie.title}
                image={imgUrl + myPath}
                description={movie.overview}
                name={movie.title}
                rating={movie.vote_average}
              />
            </div>
          );
        })}
      </div>
    );
  } else inputEnablerMsg = <p>Please! enter a search input above! :)</p>;

  return (
    <div style={{ alignContent: "center" }}>
      {/* {searchBar} */}
      {inputEnablerMsg}
      {movies}
    </div>
  );
};

export default GeneralMovies;
