import React from "react";
import classes from "./LoginSideDrawer.module.css";
// import Home from "../../components/Home/Home";
// import { Route } from "react-router-dom";
import Login from "../../containers/Login/Login";
const loginsidedrawer = (props) => {
  let attachedClasses = [classes.SideDrawer, classes.Close];
  if (props.open) {
    attachedClasses = [classes.SideDrawer, classes.Open];
  }
  return (
    <div className={attachedClasses.join(" ")} onPointerLeave={props.onLeave}>
      {/* <Route path={"/"} component={Home} /> */}
      <Login className={attachedClasses.join(" ")} />
    </div>
  );
};

export default loginsidedrawer;
