import React, { useEffect } from "react";
import Auxil from "../../../hoc/Auxil/Auxil";
const GenericMovies = (props) => {
  useEffect(
    fetchedMovies.map((Movie) => {
      return Movie;
    })
  );
  const fetchedMovies = async () => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    let finalMovies = [];
    // const API_KEY = "?api_key=d0a9ee76df15c9c8e455000b78e70a56";
    // const languageFilter = "&language=he-IL";
    // const baseSearchUrl = "https://api.themoviedb.org/3/search/movie?query=";
    // const baseUrl = "https://api.themoviedb.org/3/movie/";
    // let urlString = baseUrl.concat("top_rated", API_KEY);

    await fetch(props.urlString) // need to give urlString
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        id_info = json.results.map((result) => {
          return result.id; // returning id's of movies
        });
      })
      .catch((error) => console.log(error));
    // console.log(id_info);
    //   this.setState({
    //     movies_ids: id_info, // setting an array of id's
    //   });
    id_info.map(async (id, index) => {
      movieUrl = props.baseUrl + id + props.API_KEY; // creating a movie from the id given
      myMovies[index] = await fetch(movieUrl)
        .then((res) => res.json())
        .then((json) => {
          // console.log(json);
          return json; // returning movie at an index location
        });
      finalMovies = myMovies;
      console.log(finalMovies);
      // .catch((error) => {
      //   console.log(error);
      // })
      // this.setState({
      //   movies: myMovies,
      // });
      // console.log(this.state.movies);
    });
    console.log(myMovies);
    // return finalMovies;
  };
  return <Auxil>{props.children}</Auxil>;
};
export default GenericMovies;
