import React, { useState, useEffect } from "react";

const useFetch = (props, searchInput) => {
  const [stateIds, setStateIds] = useState([]);
  const [stateMovies, setStateMovies] = useState([]);
  const [inputEntered, setInputEntered] = useState(true);
  let myInput = searchInput;
  console.log(myInput);
  useEffect(() => {
    console.log(myInput);

    fetchingTheMovies(myInput);
  }, []);
  const fetchingTheMovies = async (searchInput) => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    let urlString = "";
    if (searchInput !== "") {
      urlString = props.baseSearchUrl.concat(searchInput, props.API_KEY_SEARCH);
      console.log("url " + urlString + "inp is: " + searchInput);
      await fetch(urlString) // need to give urlString
        .then((res) => res.json())
        .then((json) => {
          for (const [i, result] of json.results.entries()) {
            id_info[i] = result.id;
          }
        })
        .catch((error) => console.log(error));
      console.log("i am here");
      setStateIds(id_info);
      for (const [i, id] of stateIds.entries()) {
        movieUrl = props.baseMovieUrl + id + props.API_KEY_MOVIE;
        myMovies[i] = await fetch(movieUrl)
          .then((res) => res.json())
          .then((json) => {
            console.log(json);
            return json;
          });
      }
      setStateMovies((prev) => [...prev, ...myMovies]);
      console.log(myMovies);
      setInputEntered(true);
    } else setInputEntered(false);
  };
  console.log(stateMovies);
  return [stateMovies, inputEntered];
};

export default useFetch;
