import SearchBar from "../../../components/Searchbar/Searchbar";
class GenericMovies extends Component {
  state = {
    stateMovies: [],
    stateIds: [],
    inputEntered: false,
  };
  // let [stateMovies, setStateMovies] = useState([]);
  // const [stateIds, setStateIds] = useState([]);
  // const [inputEntered, setInputEntered] = useState(false);
  // useEffect(() => {}, [stateMovies]);
  searchContent = (event) => {
    let searchInput = event.target.value;
    this.fetchedMovies("captain");
  };
  fetchedMovies = async (searchInput) => {
    console.log(searchInput);
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    // let finalMovies = [];
    let urlString = "";
    // let searchInput = props.searchBar.props.onChange();
    if (searchInput !== "")
      urlString = this.props.baseSearchUrl.concat(
        searchInput,
        this.props.API_KEY_SEARCH
      );

    //   else if(){
    // }
    // else if(){}

    await fetch(urlString) // need to give urlString
      .then((res) => res.json())
      .then((json) => {
        console.log(json);
        id_info = json.results.map((result) => {
          return result.id; // returning id's of movies
        });
      })
      .catch((error) => console.log(error));
    this.setState({ setStateIds: id_info });
    // console.log(id_info);
    //   this.setState({
    //     movies_ids: id_info, // setting an array of id's
    //   });
    this.stateIds.map(async (id, index) => {
      movieUrl = this.props.baseMovieUrl + id + this.props.API_KEY_MOVIE; // creating a movie from the id given
      // console.log(movieUrl);
      myMovies[index] = await fetch(movieUrl)
        .then((res) => res.json())
        .then((json) => {
          // console.log(json);

          return json; // returning movie at an index location
        });
      // setStateMovies((stateMovies) => [...stateMovies, ...myMovies]);
      // setStateMovies((myMovies) => {
      //   return { myMovies };
      // });
      setStateMovies(myMovies);
      // setStateMovies((prevStateMovies) => {
      //   return { ...prevStateMovies, ...myMovies };
      // });
      // console.log(stateMovies);

      // setState((prevState) => {
      //   // Object.assign would also work
      //   return { ...prevState, ...updatedValues };
      // });
      // console.log(myMovies[index]);
      // finalMovies = myMovies;
      // console.log(finalMovies);
      // .catch((error) => {
      //   console.log(error);
      // })
      // this.setState({
      //   movies: myMovies,
      // });
      // console.log(this.state.movies);
    });

    // return finalMovies;

    // console.log(stmovies);
    // });
    setInputEntered(true);
    // this.setState({ inputEntered: true });
  };
  render() {
    let imgUrl = "https://image.tmdb.org/t/p/w185/";
    let movies = null;
    let myPath = "";
    let inputEnablerMsg = null;
    let searchBar = null;
    // if(this.state.)
    if (props.baseSearchUrl) searchBar = <SearchBar onChange={searchContent} />;

    if (inputEntered) {
      console.log(stateMovies);
      const presentMovies = stateMovies;

      movies = (
        <div>
          {stateMovies.map((movie, index) => {
            if (movie.poster_path) myPath = movie.poster_path;
            else if (movie.backdrop_path) myPath = movie.backdrop_path;
            else return <p key={index}> NO IMAGE! sorry :(</p>;
            return (
              // <div style={{ display: "flex" }}>
              <Movie
                // key={movie.id}
                title={movie.title}
                image={imgUrl + myPath}
                description={movie.overview}
                name={movie.title}
                rating={movie.vote_average}
              />
              // {console.log(movie.title)}
              // </div>
            );
          })}
        </div>
      );
      console.log(movies);
    } else {
      inputEnablerMsg = <p>Please! enter a search input above! :)</p>;
    }

    return (
      <div>
        {/* {props.searchBar} */}
        {props.children}
        {/* <SearchBar onChange={searchContent} /> */}
        {searchBar}
        {movies}

        {inputEnablerMsg}
      </div>
    );
  }
}
export default GenericMovies;
