import React from "react";
import classes from "./Movie.module.css";
import Rating from "./Rating/Rating";
const movie = (props) => {
  // const myClassname = props.className;
  let myClasses = classes;
  let searchedMovie = null;
  let otherMovie = null;

  if (props.isSearched)
    searchedMovie = (
      <div className={classes.movie_card}>
        <div className={classes.info_section}>
          <div className={classes.movie_header}>
            <h1>{props.title}</h1>
          </div>
          <div>
            <img className={classes.Images} src={props.image} alt="Movie" />
          </div>
          <div className={classes.rating}>
            <Rating name={props.name} rating={props.rating} />
          </div>
        </div>
        <div className={classes.movie_desc}>
          <p style={{ padding: "8px" }}>{props.description}</p>
        </div>
      </div>
    );
  else {
    myClasses = props.className;
    otherMovie = (
      <div className={classes.movie_card}>
        <div className={classes.movie_header}>{props.title}</div>
        <div className={classes.movie_content}>
          <div className={classes.movie_content_header}>
            <h3 className={movie_title}></h3>
          </div>
          <div>
            <img className={classes.Images} src={props.image} alt="Movie" />
          </div>
          <div className={classes.rating}>
            <Rating name={props.name} rating={props.rating} />
          </div>
        </div>
        <div className={classes.movie_desc}>
          <p style={{ padding: "8px" }}>{props.description}</p>
        </div>
      </div>
    );
  }
  // myClasses = props.classes;
  // const myClasses = props.classes;
  // classes = myClasses;
  return (
    <div>
      {searchedMovie}
      {otherMovie}
    </div>
  );
};

export default movie;
