import React, { useEffect, useState } from "react";
import Movie from "../../../components/Movie/Movie";
import SearchBar from "../../../components/Searchbar/Searchbar";
const GeneralMovies = (props) => {
  const [stateIds, setStateIds] = useState([]);
  const [stateMovies, setStateMovies] = useState([]);
  const [inputEntered, setInputEntered] = useState(true);
  let searchInput = "captain";
  //   const useFetch = (searchInput) => {
  //   useEffect(() => console.log(stateIds), [stateIds]);
  useEffect(() => {
    fetchingTheMovies();
  }, []);
  const searchMovie = (event) => {
    let searchInput = event.target.value;
    fetchingTheMovies(searchInput);
  };
  const fetchingTheMovies = async (searchInput) => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    let urlString = "";
    if (searchInput !== "") {
      urlString = props.baseSearchUrl.concat(searchInput, props.API_KEY_SEARCH);
      await fetch(urlString) // need to give urlString
        .then((res) => res.json())
        .then((json) => {
          for (const [i, result] of json.results.entries()) {
            id_info[i] = result.id;
          }
        })
        .catch((error) => console.log(error));
      setStateIds(id_info);
      for (const [i, id] of stateIds.entries()) {
        movieUrl = props.baseMovieUrl + id + props.API_KEY_MOVIE;
        myMovies[i] = await fetch(movieUrl)
          .then((res) => res.json())
          .then((json) => {
            return json;
          });
      }
      setStateMovies(() => myMovies);
      console.log(myMovies);
      setInputEntered(true);
    } else setInputEntered(false);
  };

  let imgUrl = "https://image.tmdb.org/t/p/w185/";
  let movies = null;
  let myPath = "";
  let inputEnablerMsg = null;
  let searchBar = null;
  // const starterFunc = () => {
  // if(this.state.)
  if (props.baseSearchUrl) searchBar = <SearchBar onChange={searchMovie} />;

  if (inputEntered) {
    console.log(stateMovies);
    movies = (
      <div>
        {stateMovies.map((movie, index) => {
          if (movie.poster_path) myPath = movie.poster_path;
          else if (movie.backdrop_path) myPath = movie.backdrop_path;
          else return <p key={index}> NO IMAGE! sorry :(</p>;
          return (
            // <div style={{ display: "flex" }}>
            <Movie
              key={movie.id}
              title={movie.title}
              image={imgUrl + myPath}
              description={movie.overview}
              name={movie.title}
              rating={movie.vote_average}
            />
            // {console.log(movie.title)}
            // </div>
          );
        })}
      </div>
    );
    console.log(movies);
  } else {
    inputEnablerMsg = <p>Please! enter a search input above! :)</p>;
  }

  return (
    <div>
      {searchBar}
      {inputEnablerMsg}
      {movies}
    </div>
  );
};

export default GeneralMovies;
