import React, { useEffect, useState } from "react";

const GeneralMovies = (props) => {
  const [stateIds, setStateIds] = useState([]);
  const [stateMovies, setStateMovies] = useState([]);
  const [inputEntered, setInputEntered] = useState(true);
  let searchInput = "hero";

  useEffect(async () => {
    let movieUrl = "";
    let myMovies = [];
    let id_info = [];
    // let myMovies = [];
    // let finalMovies = [];
    let urlString = "";
    // let searchInput = props.searchBar.props.onChange();
    console.log(props.baseSearchUrl + "api" + props.API_KEY_SEARCH);
    if (searchInput !== "")
      urlString = props.baseSearchUrl.concat(searchInput, props.API_KEY_SEARCH);

    console.log(urlString);
    await fetch(urlString) // need to give urlString
      .then((res) => res.json())
      .then((json) => {
        // console.log(json);
        for (const [i, result] of json.results.entries()) {
          // console.log("index" + i + "value" + result.id);
          id_info[i] = result.id;
          //   console.log(result);
          //   const myId = result.id;
          //   setStateIds((prevIds) => [...id_info]);
        }
        // id_info = json.results.map((result) => {
        //   return result.id; // returning id's of movies
        // });
      });
    // .catch((error) => console.log(error));
    console.log(id_info);
    // console.log(inputReady);
    for (const [i, id] of id_info.entries()) {
      movieUrl = props.baseMovieUrl + id + props.API_KEY_MOVIE;
      //   console.log("i? " + i + " val? " + id);
      myMovies[i] = await fetch(movieUrl)
        .then((res) => res.json())
        .then((json) => {
          // console.log(json);
          //   console.log("index " + i + "val " + json);
          return json;
        });
      //   setStateMovies(myMovies);
      //   console.log(stateMovies);
    }
    console.log(myMovies);
    setStateMovies(() => myMovies);
    // console.log(stateMovies);
    setInputEntered(true);
  }, []);
  return <div></div>;
};

export default GeneralMovies;
